import { Component, computed, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

type MenuItem = { id: number; name: string; price: number };
type OrderItem = { id?: number; itemName: string; quantity: number; total: number; userName?: string; tableNumber?: number; orderTime?: string };

@Component({
  selector: 'app-dashboard',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="container">
      <div class="card">
        <h2>Add New Menu Item</h2>
        <form (ngSubmit)="addMenu()">
          <input [(ngModel)]="newItem.name" name="name" placeholder="Item Name" required />
          <input [(ngModel)]="newItem.price" type="number" name="price" placeholder="Price" min="1" step="0.01" required />
          <button class="btn" type="submit">Add Item</button>
        </form>
      </div>

      <div class="card">
        <h2>Today's Menu</h2>
        <div class="grid menu">
          <div class="card" *ngFor="let item of menu()">
            <h3 style="margin:0 0 4px 0">{{ item.name }}</h3>
            <div>₹{{ item.price }}</div>
            <button class="btn secondary" (click)="addToOrder(item)">Add</button>
          </div>
        </div>
      </div>

      <div class="card">
        <h2>Current Order</h2>
        <div *ngFor="let i of currentOrder()">{{ i.itemName }} x{{ i.quantity }}</div>
        <p><b>Total:</b> ₹{{ total() | number:'1.2-2' }}</p>
        <form (ngSubmit)="placeOrder()">
          <input [(ngModel)]="customerName" name="userName" placeholder="Customer Name" required />
          <select [(ngModel)]="tableNumber" name="tableNumber" required>
            <option value="" disabled>Select Table</option>
            <option *ngFor="let t of tables" [value]="t">Table {{ t }}</option>
          </select>
          <button class="btn" type="submit">Place Order</button>
        </form>
      </div>

      <div class="card">
        <h2>Order History</h2>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Customer</th>
              <th>Table</th>
              <th>Items</th>
              <th>Total</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let o of orders()">
              <td>{{ o.id }}</td>
              <td>{{ o.userName }}</td>
              <td>{{ o.tableNumber }}</td>
              <td>{{ o.itemName }} x{{ o.quantity }}</td>
              <td>₹{{ o.total }}</td>
              <td>{{ o.orderTime }}</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  `
})
export class DashboardComponent {
  constructor(private http: HttpClient) {
    this.loadMenu();
    this.loadOrders();
  }

  tables = [1,2,3,4,5];
  menu = signal<MenuItem[]>([]);
  orders = signal<OrderItem[]>([]);
  currentOrder = signal<OrderItem[]>([]);

  newItem: { name: string; price: number | null } = { name: '', price: null };
  customerName = '';
  tableNumber: number | null = null;

  total = computed(() => this.currentOrder().reduce((s, i) => s + i.total, 0));

  loadMenu() {
    this.http.get<MenuItem[]>('/api/canteen/menu').subscribe(items => this.menu.set(items));
  }

  loadOrders() {
    this.http.get<OrderItem[]>('/api/canteen/orders').subscribe(items => this.orders.set(items));
  }

  addMenu() {
    const name = this.newItem.name?.trim();
    const price = Number(this.newItem.price);
    if (!name || !price || price <= 0) return;
    this.http.post<MenuItem>('/api/canteen/menu', { name, price }).subscribe(() => {
      this.newItem = { name: '', price: null };
      this.loadMenu();
    });
  }

  addToOrder(item: MenuItem) {
    const exists = this.currentOrder().find(i => i.itemName === item.name);
    if (exists) {
      exists.quantity += 1;
      exists.total = exists.quantity * item.price;
      this.currentOrder.set([...this.currentOrder()]);
    } else {
      this.currentOrder.set([...this.currentOrder(), { itemName: item.name, quantity: 1, total: item.price }]);
    }
  }

  placeOrder() {
    if (!this.customerName || !this.tableNumber || this.currentOrder().length === 0) return;
    const payload: OrderItem = {
      userName: this.customerName,
      tableNumber: Number(this.tableNumber),
      itemName: this.currentOrder().map(i => i.itemName).join(', '),
      quantity: this.currentOrder().reduce((s, i) => s + i.quantity, 0),
      total: this.total()
    };

    this.http.post<OrderItem>('/api/canteen/order', payload).subscribe(() => {
      this.currentOrder.set([]);
      this.customerName = '';
      this.tableNumber = null;
      this.loadOrders();
    });
  }
}
